import api from './index';
import { IUser } from '../types/types';
export class UserApi {
  static async getAllUsers():Promise<IUser[] | null>  {
    try{
      const users =  (await api.get('/users')).data
      console.log(users)
      return users;
    } catch (err){
      console.log(err);
      return null;
    }
  }
}
