import {ITodo} from "../../types/types";

interface ITodoProps {
    todo: ITodo;
    deleteTodo: (arg0: string) => void;
}
export const TodoItem = ({ todo }: ITodoProps) => {
    return (
        <li className='list-group-item d-flex justify-content-between align-items-canter'>
            <div className="">
                <span>{todo.title}</span>
            </div>
            <div className="">
                <button className="btn btn-primary" style={{marginRight: '10px'}}>Изменить</button>
                <button className="btn btn-success" style={{marginRight: '10px'}}>Завершить</button>
                <button className="btn btn-danger">Удалить</button>
            </div>
        </li>
    )
}
