import { ChangeEvent, FC, FormEvent, useState } from 'react';
import { showAlert } from '../../redux/Actions';
import { useDispatch, useSelector } from 'react-redux';
import { Alert } from '../todos/Alert/alert';
import { IAlertReducer } from '../../types/types';
import * as domain from 'domain';
import { Tabs } from 'antd';
import { Login } from './login';
import { Register } from './register';

export const Authorization: FC = () => {
  const dispatch = useDispatch()
  const alertState = useSelector((state: IAlertReducer) => state.alertReducer)
  const [username, setUsername] = useState<string>('')
  const [password, setPassword] = useState<string>('')
  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    if(!username.trim()) {
      dispatch(showAlert('Укажите все данные', 'warning'))
      setUsername('')
      return
    }
    if(!password.trim()) {
      dispatch(showAlert('Укажите все данные', 'warning'))
      setPassword('')
      return
    }
  }

  const handleChangeInputValueName = (event: ChangeEvent<HTMLInputElement>) => {
    setUsername(event.target.value)
  }
  const handleChangeInputValuePassword = (event: ChangeEvent<HTMLInputElement>) => {
    setPassword(event.target.value)
  }
  return (
    <center>
      <main style={{width: 400, margin: "50 auto"}}>
        <Tabs
          items={[
            {
              label: "Войти",
              key: "1",
              children: <Login />
            },
            {
              label: "Регистрация",
              key: "2",
              children: <Register />
            },
          ]}
        />
      </main>
    </center>
  )
}
